function calculateInvoice() {
  const rows = document.querySelectorAll("#invoice-body tr");
  let total = 0;

  rows.forEach((row) => {
    const qty = parseFloat(row.querySelector(".qty").value) || 0;
    const rate = parseFloat(row.querySelector(".rate").value) || 0;
    const amount = qty * rate;
    row.querySelector(".amount").value = amount.toFixed(2);
    total += amount;
  });

  const cgst = total * 0.09;
  const sgst = total * 0.09;
  const totalGst = cgst + sgst;
  const finalTotal = total + totalGst;

  document.getElementById("totalAmount").value = total.toFixed(2);
  document.getElementById("cgst").value = cgst.toFixed(2);
  document.getElementById("sgst").value = sgst.toFixed(2);
  document.getElementById("totalGst").value = totalGst.toFixed(2);
  document.getElementById("finalTotal").value = finalTotal.toFixed(2);
}

document.querySelectorAll(".qty, .rate").forEach((input) => {
  input.addEventListener("input", calculateInvoice);
});

// Initial calculation
calculateInvoice();

document.getElementById("addRowBtn").addEventListener("click", () => {
  const tbody = document.getElementById("invoice-body");
  const newRow = document.createElement("tr");

  newRow.innerHTML = `
      <td>${tbody.rows.length + 1}</td>
      <td><input type="text" class="hsn" /></td>
      <td><input type="text" class="desc" /></td>
      <td><input type="number" class="qty" /></td>
      <td><input type="number" class="rate" /></td>
      <td><input type="text" class="amount" readonly /></td>
    `;

  tbody.appendChild(newRow);

  // Re-bind event listeners for new row
  newRow.querySelectorAll(".qty, .rate").forEach((input) => {
    input.addEventListener("input", calculateInvoice);
  });

  calculateInvoice();
});

document.getElementById("cancelBtn").addEventListener("click", () => {
  if (confirm("Do you want to remove the last item?")) {
    const tbody = document.getElementById("invoice-body");
    if (tbody.rows.length > 1) {
      tbody.deleteRow(-1);
      calculateInvoice();
    } else {
      alert("At least one row must remain.");
    }
  }
});

function numberToWords(num) {
  const a = [
    "",
    "one",
    "two",
    "three",
    "four",
    "five",
    "six",
    "seven",
    "eight",
    "nine",
    "ten",
    "eleven",
    "twelve",
    "thirteen",
    "fourteen",
    "fifteen",
    "sixteen",
    "seventeen",
    "eighteen",
    "nineteen",
  ];
  const b = [
    "",
    "",
    "twenty",
    "thirty",
    "forty",
    "fifty",
    "sixty",
    "seventy",
    "eighty",
    "ninety",
  ];

  function inWords(n) {
    if (n === 0) return "zero";
    if (n < 20) return a[n];
    if (n < 100) return b[Math.floor(n / 10)] + (n % 10 ? "-" + a[n % 10] : "");
    if (n < 1000)
      return (
        a[Math.floor(n / 100)] +
        " hundred " +
        (n % 100 ? "and " + inWords(n % 100) : "")
      );
    if (n < 100000)
      return inWords(Math.floor(n / 1000)) + " thousand " + inWords(n % 1000);
    if (n < 10000000)
      return inWords(Math.floor(n / 100000)) + " lakh " + inWords(n % 100000);
    return (
      inWords(Math.floor(n / 10000000)) + " crore " + inWords(n % 10000000)
    );
  }

  return inWords(num).replace(/\s+/g, " ").trim() + " rupees only";
}

function calculateInvoice() {
  const rows = document.querySelectorAll("#invoice-body tr");
  let total = 0;

  rows.forEach((row) => {
    const qty = parseFloat(row.querySelector(".qty").value) || 0;
    const rate = parseFloat(row.querySelector(".rate").value) || 0;
    const amount = qty * rate;
    row.querySelector(".amount").value = amount.toFixed(2);
    total += amount;
  });

  const cgst = total * 0.09;
  const sgst = total * 0.09;
  const totalGst = cgst + sgst;
  const finalTotal = total + totalGst;

  document.getElementById("totalAmount").value = total.toFixed(2);
  document.getElementById("cgst").value = cgst.toFixed(2);
  document.getElementById("sgst").value = sgst.toFixed(2);
  document.getElementById("totalGst").value = totalGst.toFixed(2);
  document.getElementById("finalTotal").value = finalTotal.toFixed(2);

  // Convert to words
  const roundedAmount = Math.round(finalTotal);
  document.getElementById("amountInWords").innerText =
    numberToWords(roundedAmount);
}

// Add event listeners
document.querySelectorAll(".qty, .rate").forEach((input) => {
  input.addEventListener("input", calculateInvoice);
});

// Initial call
calculateInvoice();

