// Function to clear all inputs
function clearAllInputs() {
  // Clear header inputs
  document.getElementById("bill-to").value = "";
  document.getElementById("address").value = "";
  document.getElementById("mobile").value = "";
  document.getElementById("gst").value = "";
  document.getElementById("invoice-no").value = "";
  document.getElementById("invoice-date").value = "";

  // Clear table inputs
  const tableInputs = document.querySelectorAll("#invoice-body input");
  tableInputs.forEach((input) => {
    input.value = "";
  });

  // Clear total fields
  document.getElementById("totalAmount").value = "";
  document.getElementById("cgst").value = "";
  document.getElementById("sgst").value = "";
  document.getElementById("totalGst").value = "";
  document.getElementById("finalTotal").value = "";
  document.getElementById("amountInWords").innerText = "Zero Rupees Only";
}

// Add click event listener to Clear button
document.getElementById("clearBtn").addEventListener("click", clearAllInputs);

// Add smooth scrolling to headers
document.querySelectorAll('[id$="-header"]').forEach((header) => {
  header.style.cursor = "pointer";
  header.addEventListener("click", () => {
    header.scrollIntoView({ behavior: "smooth" });
  });
});

const inputs = document.querySelectorAll("#addressForm input");
const downloadBtn = document.getElementById("downloadBtn");

function validateFormFields() {
  let isValid = true;

  inputs.forEach((input) => {
    if (input.id === "pincode") {
      const pin = input.value.trim();
      if (!/^\d{6}$/.test(pin)) {
        isValid = false;
      }
    } else if (input.value.trim() === "") {
      isValid = false;
    }
  });

  downloadBtn.disabled = !isValid;
}

// Attach input listeners
inputs.forEach((input) => {
  input.addEventListener("input", validateFormFields);
});

// Optional: Clear button functionality
document.getElementById("clearBtn").addEventListener("click", () => {
  inputs.forEach((input) => (input.value = ""));
  validateFormFields(); // Re-validate after clearing
});
